local._http.request = function (xhr, onResponse) {
/*
 * this function will emulate in browser, node's http.request function
 * https://nodejs.org/dist/v0.12.18/docs/api/all.html#all_http_request_options_callback
 */
    let bufList;
    let data;
    let handler;
    let isDone;
    let req;
    let res;
    xhr = {
        end: function (_data) {
            if (isDone) {
                return;
            }
            isDone = true;
            data = _data;
            // async send req from client to server
            setTimeout(function () {
                local.serverLocalReqHandler(req, res);
            });
        },
        headers: xhr.headers,
        method: xhr.method,
        on: function () {
            return xhr;
        },
        timeout: xhr.timeout,
        url: xhr.href
    };
    bufList = [];
    handler = new globalThis.EventTarget();
    req = {
        emit: function (type, data) {
            handler.dispatchEvent(new globalThis.CustomEvent("req." + type, {
                detail: data
            }));
        },
        headers: xhr.headers,
        httpVersion: "1.1",
        method: xhr.method,
        on: function (type, onEvent) {
            handler.addEventListener("req." + type, function (evt) {
                onEvent(evt.detail);
            });
            if (req.readable && type === "end") {
                req.readable = null;
                req.emit("data", data);
                req.emit("end");
            }
            return req;
        },
        pipe: function (writable) {
            req.on("data", function (buf) {
                writable.write(buf);
            });
            req.on("end", function () {
                writable.end();
            });
            return writable;
        },
        readable: true,
        url: xhr.url
    };
    res = {
        emit: function (type, data) {
            handler.dispatchEvent(new globalThis.CustomEvent("res." + type, {
                detail: data
            }));
        },
        end: function (data) {
            if (res._isDone) {
                return;
            }
            res._isDone = true;
            bufList.push(data);
            // notify server res is finished
            res.emit("finish");
            // pass res to client
            onResponse(res);
            res.emit("data", local.bufferConcat(bufList));
            res.emit("end");
        },
        on: function (type, onEvent) {
            handler.addEventListener("res." + type, function (evt) {
                onEvent(evt.detail);
            });
            return res;
        },
        setHeader: function (key, val) {
            xhr.resHeaders[key.toLowerCase()] = val;
        },
        statusCode: 200,
        write: function (data) {
            bufList.push(data);
        }
    };
    return xhr;
};

